public class Account
{
    private BigDecimal balance;

    public BigDecimal getBalance()
    {
        return balance;
    }

    public void setBalance(final BigDecimal balance)
    {
        this.balance = balance;
    }
}
public class AccountService
{
    public void transferMoney(final Account from, final Account to, final BigDecimal amount)
    {
        if (from.getBalance().compareTo(amount) < 0)
        {
            return;
        }

        if (amount.compareTo(BigDecimal.ZERO) <= 0)
        {
            return;
        }

        final BigDecimal newFromBalance = from.getBalance().subtract(amount);
        from.setBalance(newFromBalance);

        final BigDecimal newToBalance = to.getBalance().add(amount);
        to.setBalance(newToBalance);
    }
}

