async function safeUpdate(doc, text) {
  const result = await collection.updateOne(
    { 
      _id: doc._id,
      updatedAt: doc.updatedAt  // Only match if timestamp hasn't changed
    },
    { 
      $set: { 
        text: text,
        updatedAt: new Date()  // Update timestamp to new value
      } 
    }
  );

  // If no document was matched, it means someone else updated it
  if (result.matchedCount === 0) {
    throw new Error('Document was modified by another process. Please retry with fresh data.');
  }

  return result;
}

