public class MoneyTransfer
{
    public void transfer(final Account from, final Account to, final BigDecimal amount)
    {
        synchronized (min(from, to))
        {
            synchronized (max(from, to))
            {
                from.debit(amount);
                to.credit(amount);
            }
        }
    }
}
